# Dungeon Generator
import random

ROOMS = 25
MIN_SIZE = 4
MAX_SIZE = 12

def roll_dice(n, sides):
    return sum(random.randint(1, sides) for _ in range(n))

def gen_room(room_id):
    w = random.randint(MIN_SIZE, MAX_SIZE)
    h = random.randint(MIN_SIZE, MAX_SIZE)
    x = random.randint(0, 80 - w)
    y = random.randint(0, 50 - h)
    return {"id": room_id, "x": x, "y": y, "w": w, "h": h}

def place_treasure(room):
    gold = roll_dice(3, 6) * 10
    gems = roll_dice(1, 4)
    return {"gold": gold, "gems": gems}

def spawn_monster(room, difficulty):
    hp = roll_dice(difficulty, 8)
    atk = roll_dice(difficulty, 6)
    return {"hp": hp, "atk": atk, "room": room["id"]}

def connect_rooms(a, b):
    mid_x = (a["x"] + b["x"]) // 2
    mid_y = (a["y"] + b["y"]) // 2
    return {"from": a["id"], "to": b["id"]}

def generate_dungeon():
    rooms = [gen_room(i) for i in range(ROOMS)]
    for room in rooms:
        place_treasure(room)
        spawn_monster(room, difficulty=3)
    return rooms
